mapper.update:
UPDATE comments
post_id = #{postId}, user_id = #{userId}, parent_id = #{parentId}, content = #{content}, status = #{status}, like_count = #{likeCount}, created_at = #{createdAt},
WHERE id = #{id}

mapper.select:
SELECT id, post_id, user_id, parent_id, content, status, like_count, created_at
FROM comments
WHERE user_id = #{userId}
ORDER BY created_at DESC
LIMIT #{size} OFFSET #{offset}

SELECT id, post_id, user_id, parent_id, content, status, like_count, created_at
FROM comments

    post_id = #{postId}

        AND parent_id = #{parentId}


        AND parent_id IS NULL


ORDER BY created_at ASC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(1)
FROM comments

    post_id = #{postId}

        AND parent_id = #{parentId}


        AND parent_id IS NULL

mapper.update:
UPDATE comments
SET like_count = IFNULL(like_count, 0) + 1
WHERE id = #{id}

UPDATE comments
SET like_count = GREATEST(IFNULL(like_count, 0) - 1, 0)
WHERE id = #{id}
