mapper.update:
UPDATE likes
user_id = #{userId}, target_type = #{targetType}, target_id = #{targetId}, created_at = #{createdAt},
WHERE id = #{id}

mapper.select:
SELECT id, user_id, target_type, target_id, created_at
FROM likes
WHERE user_id = #{userId}
  AND target_type = #{targetType}
  AND target_id = #{targetId}
LIMIT 1

mapper.delete:
DELETE FROM likes
WHERE user_id = #{userId}
  AND target_type = #{targetType}
  AND target_id = #{targetId}
