mapper.update:
UPDATE notifications
user_id = #{userId}, type = #{type}, title = #{title}, content = #{content}, ref_type = #{refType}, ref_id = #{refId}, is_read = #{isRead}, created_at = #{createdAt},
WHERE id = #{id}

mapper.select:
SELECT id, user_id, type, title, content, ref_type, ref_id, is_read, created_at
FROM notifications

    user_id = #{userId}

        AND is_read = #{isRead}


ORDER BY created_at DESC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(1)
FROM notifications

    user_id = #{userId}

        AND is_read = #{isRead}
