mapper.update:
UPDATE reports
reporter_id = #{reporterId}, target_type = #{targetType}, target_id = #{targetId}, reason = #{reason}, detail = #{detail}, status = #{status}, handled_by = #{handledBy}, handled_at = #{handledAt}, result = #{result}, created_at = #{createdAt},
WHERE id = #{id}

mapper.select:
SELECT id, reporter_id, target_type, target_id, reason, detail, status,
       handled_by, handled_at, result, created_at
FROM reports

    reporter_id = #{reporterId}

        AND status = #{status}


ORDER BY created_at DESC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(1)
FROM reports

    reporter_id = #{reporterId}

        AND status = #{status}

SELECT id, reporter_id, target_type, target_id, reason, detail, status,
       handled_by, handled_at, result, created_at
FROM reports


        status = #{status}


ORDER BY created_at DESC
LIMIT #{size} OFFSET #{offset}
